mapper.update:
UPDATE user_behavior
SET user_id = #{user_id},
    tags = #{tags},
    behavior_type = #{behavior_type}
WHERE id = #{id};

mapper.select:
SELECT id, user_id, tags, behavior_type
FROM user_behavior
WHERE id = #{id};

SELECT id, user_id, tags, behavior_type
FROM user_behavior
WHERE user_id = #{user_id};
